Scripts.OnEvent:

if (event == "VARIABLES_LOADED") then 
   RaidMaker_OnLoad(); 
elseif (event == "GUILD_ROSTER_UPDATE") then 
   RaidMaker_GuildRosterUpdate(flag); 
elseif (event == "PARTY_MEMBERS_CHANGED") then 
   RaidMaker_handle_PARTY_MEMBERS_CHANGED(); 
elseif (event == "CALENDAR_OPEN_EVENT") then 
   RaidMaker_handle_CALENDAR_OPEN_EVENT(...); 
elseif (event == "CHAT_MSG_LOOT") then 
   RaidMaker_handle_CHAT_MSG_LOOT(...); 
elseif (event == "LOOT_OPENED") then 
   RaidMaker_handle_LOOT_OPENED(...); 
elseif (event == "CHAT_MSG_SYSTEM") then 
   RaidMaker_handle_CHAT_MSG_SYSTEM(...); 
elseif (event == "CHAT_MSG_GUILD") then 
   RaidMaker_handle_CHAT_MSG_GUILD(...); 
elseif (event == "CHAT_MSG_RAID") then 
   RaidMaker_handle_CHAT_MSG_RAID(...); 
elseif (event == "CHAT_MSG_SAY") then 
   RaidMaker_handle_CHAT_MSG_SAY(...); 
elseif (event == "CHAT_MSG_PARTY") then 
   RaidMaker_handle_CHAT_MSG_PARTY(...); 
elseif (event == "CHAT_MSG_PARTY_LEADER") then 
   RaidMaker_handle_CHAT_MSG_PARTY_LEADER(...); 
elseif (event == "CHAT_MSG_RAID_LEADER") then 
   RaidMaker_handle_CHAT_MSG_RAID_LEADER(...); 
elseif (event == "CHAT_MSG_RAID_WARNING") then 
   RaidMaker_handle_CHAT_MSG_RAID_WARNING(...); 
elseif (event == "CHAT_MSG_ADDON") then 
   RaidMaker_handle_CHAT_MSG_ADDON(...); 
end
